/**
 * Extracts the transcript text from a full HTML string using robust regular expressions.
 * This function is designed to be resilient to minor changes in HTML attributes and formatting, including newlines.
 * @param {string} html - The full HTML content as a string.
 * @returns {string} The cleaned, full transcript or an error message.
 */
function extractTranscript(html) {
  // 1. Find the main transcript container using the robust ID selector.
  const divRegex = /<div[^>]*id="transcript"[^>]*>(.*?)<\/div>/s;
  const divMatch = html.match(divRegex);

  if (!divMatch || !divMatch[1]) {
    return "ERROR: Transcript container div with id='transcript' was not found.";
  }

  const transcriptContent = divMatch[1];

  // 2. Find all transcript segments, allowing for newlines inside the tags.
  const segmentRegex = /<span.*?class="transcript-segment".*?>(.*?)<\/span>/gs;

  // 3. Use matchAll to get every segment.
  const allMatches = transcriptContent.matchAll(segmentRegex);

  let fullTranscript = "";

  // 4. Loop through all the found segments.
  for (const match of allMatches) {
    const segmentText = match[1];
    const cleanedText = segmentText.replace(/<\/?[^>]+(>|$)/g, " ").trim();
    if (cleanedText) {
        fullTranscript += cleanedText + " ";
    }
  }

  // 5. Check if we actually found any transcript text with the new regex.
  if (fullTranscript.trim() === "") {
    return "ERROR: The transcript div was found, but no text segments could be extracted. The segment regex failed.";
  }

  // 6. Final cleanup of any extra whitespace and return the result.
  return fullTranscript.replace(/\s+/g, ' ').trim();
}


