// Get the output from the direct input (the HTTP Request node)
const httpItems = $input.all();
// Get the original data from the 'Loop Over Items' node before the HTTP Request was made.
const originalItems = $('Loop Over Items').all();

const results = [];

// Use a classic for loop with an index to match the items from both inputs.
for (let i = 0; i < httpItems.length; i++) {
  const httpItem = httpItems[i];
  const originalItem = originalItems[i];

  // This is the new, clean JSON object we will build.
  // It starts with all the data from the 'Loop Over Items' node.
  const newJson = { ...originalItem.json };

  const html = httpItem.json.data;

  // Check if the HTML data exists in the current item
  if (!html) {
    newJson.full_transcript = null;
    newJson.error = "No HTML provided in the input item.";
  } else {
    try {
      // Run the extraction function
      const transcriptResult = extractTranscript(html);

      // Check if our function returned a controlled error message
      if (transcriptResult.startsWith("ERROR:")) {
        newJson.full_transcript = null;
        newJson.error = transcriptResult;
      } else {
        // Success! Add the full_transcript to our new object
        newJson.full_transcript = transcriptResult;
        newJson.error = null;
      }
    } catch (error) {
      // This catches unexpected script errors
      newJson.full_transcript = null;
      newJson.error = `CRITICAL SCRIPT ERROR: ${error.message}`;
    }
  }

  // Add the newly constructed object to our results array.
  results.push({ json: newJson });
}

// Return the clean list of results.
return results;

