// --- Configuration ---
const TELEGRAM_MAX_LENGTH = 4000;
// --- End Configuration ---

/**
 * Escapes characters that have special meaning in Telegram's HTML parse mode.
 * @param {string} text - The text to escape.
 * @returns {string} - The escaped text.
 */
function escapeTelegramHTML(text) {
  if (typeof text !== 'string' || !text) {
    return '';
  }
  // For HTML mode, we only need to escape these three characters.
  return text.replace(/&/g, '&')
             .replace(/</g, '<')
             .replace(/>/g, '>');
}

const inputItems = $input.all();
const originalItems = $('Loop Over Items').all();
const allTelegramMessages = [];

for (let i = 0; i < inputItems.length; i++) {
    const item = inputItems[i];
    const originalItem = originalItems[i];
    const mergedJson = { ...originalItem.json, ...item.json };
    const currentVideoId = mergedJson.id;
    const data = mergedJson.output || {};

    const getSafeArray = (arr) => Array.isArray(arr) ? arr : [];

    // --- Render Sections using HTML tags and the new escaping function ---

    const videoTitle = mergedJson.title || data.video_identification?.video_title || 'N/A';
    const channelName = mergedJson.channel_name || data.video_identification?.channel_name || 'N/A';
    const publishDate = mergedJson.publish_date || data.video_identification?.publish_date || 'N/A';
    const videoIdentification = `üé• <b>Video Identification:</b>\n‚Ä¢ <b>Video Title:</b> ${escapeTelegramHTML(videoTitle)}\n‚Ä¢ <b>Channel Name:</b> ${escapeTelegramHTML(channelName)}\n‚Ä¢ <b>Publish Date:</b> ${escapeTelegramHTML(publishDate)}`;

    const coreData = data.core_topics_and_thesis || {};
    const mainSubjects = getSafeArray(coreData.main_subjects_discussed).map(s => escapeTelegramHTML(s));
    const centralThesis = escapeTelegramHTML(coreData.central_thesis_or_argument || 'N/A');
    const coreTopicsThesis = `üí° <b>Core Topic(s) & Thesis:</b>\n‚Ä¢ <b>Core Topic(s):</b>\n${mainSubjects.length > 0 ? mainSubjects.map(s => `  ‚Ä¢ ${s}`).join('\n') : '  ‚Ä¢ N/A'}\n‚Ä¢ <b>Thesis:</b> ${centralThesis}`;

    const kdpData = data.key_discussion_points_and_evidence || {};
    const primaryArgs = getSafeArray(kdpData.primary_arguments_or_insights).map(p => escapeTelegramHTML(p));
    const mainTakeaways = getSafeArray(kdpData.main_takeaways_for_investors).map(t => escapeTelegramHTML(t));
    const keyDiscussionPoints = `üí¨ <b>Key Discussion Points & Evidence:</b>\n‚Ä¢ <b>Primary Arguments/Insights:</b>\n${primaryArgs.length > 0 ? primaryArgs.map(p => `  ‚Ä¢ ${p}`).join('\n') : '  ‚Ä¢ N/A'}\n‚Ä¢ <b>Main Takeaways for Investors:</b>\n${mainTakeaways.length > 0 ? mainTakeaways.map(t => `  ‚Ä¢ ${t}`).join('\n') : '  ‚Ä¢ N/A'}`;

    const assetsArray = getSafeArray(data.assets_mentioned_details);
    const assetsMentioned = `üí∞ <b>Assets Mentioned & Analysis Context:</b>\n${assetsArray.length > 0 ? assetsArray.map(asset => `‚Ä¢ <b>Asset:</b> ${escapeTelegramHTML(asset.asset_name_or_ticker || 'N/A')}\n  ‚Ä¢ <b>Discussion Summary:</b> ${escapeTelegramHTML(asset.discussion_summary_and_analysis || 'N/A')}`).join('\n\n') : '‚Ä¢ No specific assets mentioned or analyzed.'}`;

    const predData = data.predictions_and_recommendations || {};
    const predictions = getSafeArray(predData.specific_predictions_or_forecasts).map(p => escapeTelegramHTML(p));
    const actionsSuggested = getSafeArray(predData.investment_actions_suggested).map(a => escapeTelegramHTML(a));
    const rationaleForRecs = escapeTelegramHTML(predData.overall_rationale_for_outlook || 'N/A');
    const biasesRisks = getSafeArray(predData.identified_biases_risks_limitations).map(b => escapeTelegramHTML(b));
    const predictionsRecommendations = `üîÆ <b>Predictions/Recommendations & Rationale:</b>\n‚Ä¢ <b>Specific Predictions/Forecasts:</b>\n${predictions.length > 0 ? predictions.map(p => `  ‚Ä¢ ${p}`).join('\n') : '  ‚Ä¢ N/A'}\n‚Ä¢ <b>Investment Actions Suggested:</b>\n${actionsSuggested.length > 0 ? actionsSuggested.map(a => `  ‚Ä¢ ${a}`).join('\n') : '  ‚Ä¢ N/A'}\n‚Ä¢ <b>Overall Rationale:</b> ${rationaleForRecs}\n‚Ä¢ <b>Identified Biases/Risks:</b>\n${biasesRisks.length > 0 ? biasesRisks.map(b => `  ‚Ä¢ ${b}`).join('\n') : '  ‚Ä¢ N/A'}`;

    const sentimentData = data.overall_sentiment_and_tone || {};
    const sentimentText = escapeTelegramHTML(sentimentData.sentiment_expressed || 'N/A');
    let sentimentEmoji = '‚ö™Ô∏è';
    if (sentimentText.toLowerCase().includes('bullish') || sentimentText.toLowerCase().includes('optimistic')) sentimentEmoji = 'üü¢';
    else if (sentimentText.toLowerCase().includes('bearish') || sentimentText.toLowerCase().includes('cautious')) sentimentEmoji = 'üî¥';
    else if (sentimentText.toLowerCase().includes('neutral') || sentimentText.toLowerCase().includes('mixed')) sentimentEmoji = 'üü°';
    const overallSentimentTone = `üìä <b>Overall Sentiment & Tone:</b>\n‚Ä¢ <b>Sentiment:</b> ${sentimentEmoji} ${sentimentText}\n‚Ä¢ <b>Analytical Tone:</b> ${escapeTelegramHTML(sentimentData.analytical_tone_observed || 'N/A')}\n‚Ä¢ <b>Analytical Rigor:</b> ${escapeTelegramHTML(sentimentData.analytical_rigor_notes || 'N/A')}`;

    const executiveSummary = `üìù <b>Executive Summary:</b>\n${escapeTelegramHTML(data.executive_summary || 'N/A')}`;

    const fullMessage = [
        videoIdentification,
        coreTopicsThesis,
        keyDiscussionPoints,
        assetsMentioned,
        predictionsRecommendations,
        overallSentimentTone,
        executiveSummary
    ].join('\n\n‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ\n\n');

    // Use the same robust splitting logic
    if (fullMessage.length <= TELEGRAM_MAX_LENGTH) {
        allTelegramMessages.push({ json: { text: fullMessage, video_id: currentVideoId } });
    } else {
        let remainingMessage = fullMessage;
        let messageCounter = 1;

        while (remainingMessage.length > 0) {
            let part;
            const prefix = `<b>(Part ${messageCounter++})</b>\n\n`;
            const effectiveMaxLength = TELEGRAM_MAX_LENGTH - prefix.length;

            if (remainingMessage.length <= effectiveMaxLength) {
                part = remainingMessage;
                remainingMessage = '';
            } else {
                let splitPoint = remainingMessage.substring(0, effectiveMaxLength).lastIndexOf('\n\n');
                if (splitPoint === -1) { splitPoint = remainingMessage.substring(0, effectiveMaxLength).lastIndexOf('\n'); }
                if (splitPoint === -1) { splitPoint = remainingMessage.substring(0, effectiveMaxLength).lastIndexOf(' '); }
                if (splitPoint === -1) { splitPoint = effectiveMaxLength; }
                part = remainingMessage.substring(0, splitPoint);
                remainingMessage = remainingMessage.substring(splitPoint).trim();
            }
            allTelegramMessages.push({ json: { text: prefix + part, video_id: currentVideoId } });
        }
    }
}

return allTelegramMessages;
