const inputItems = $input.all();
const mergeItems = $('Loop Over Items').all();
const results = [];

for (let i = 0; i < inputItems.length; i++) {
    const item = inputItems[i];
    const mergeItem = mergeItems[i];

    // Find the AUDIO token count in promptTokensDetails
    const audioTokenDetail = item.json.usageMetadata.promptTokensDetails.find(
        detail => detail.modality === "AUDIO"
    );
    const candidatesTokenCount = audioTokenDetail
        ? parseInt(audioTokenDetail.tokenCount, 10)
        : 0; // Default to 0 if not found

    // Get max_tokens from the corresponding merge item
    const maxOutputTokens = parseInt(mergeItem.json.max_tokens, 10);

    let reason = "";
    if (candidatesTokenCount >= maxOutputTokens) {
        reason = "MAX_TOKENS";
    } else {
        reason = "STOP";
    }

    results.push({
        json: {
            ...item.json,
            ...mergeItem.json,
            candidatesTokenCount,
            maxOutputTokens,
            reason
        }
    });
}

return results;

// Get the transcript from the 'If' node
const transcriptFromIf = $('If').first().json.candidates[0].content.parts[0].text;

// Get the transcript from the 'HTTP Request' node
const transcriptFromHttp = $('GeminiTranscript2').first().json.candidates[0].content.parts[0].text;

// Concatenate the two transcripts (add a separator if you want)
const fullTranscript = transcriptFromIf + ' ' + transcriptFromHttp;

// Optionally, preserve all previous fields from one of the nodes (e.g., from 'If')
return [
  {
    json: {
      ...$('If').first().json, // or ...$('HTTP Request to Google').first().json
      FullTranscript: fullTranscript
    }
  }
];

