// --- Configuration ---
const TELEGRAM_MAX_LENGTH = 4000;
// --- End Configuration ---

const inputItems = $input.all();
const splitItems = $("Split Out").all();
const allTelegramMessages = [];

for (let i = 0; i < inputItems.length; i++) {
    const item = inputItems[i];
    const splitoutItem = splitItems[i];
    const mergedJson = { ...item.json, ...(splitoutItem ? splitoutItem.json : {}) };
    const currentVideoId = mergedJson.Video_id;
    const data = mergedJson.output || {}; // Gemini's output

    // Helper function to safely get an array
    const getSafeArray = (arr) => Array.isArray(arr) ? arr : [];

    // --- Render Sections based on the SIMPLIFIED SCHEMA ---

    // Video Identification
    const vidIdentData = data.video_identification || {};
    const videoTitle = (mergedJson.title && mergedJson.title !== "Not available in transcript" && mergedJson.title !== "Not provided in transcript") ? mergedJson.title : vidIdentData.video_title;
    const channelName = (mergedJson.channel_name && mergedJson.channel_name !== "Not available in transcript" && mergedJson.channel_name !== "Not provided in transcript") ? mergedJson.channel_name : vidIdentData.channel_name;
    const publishDate = (mergedJson.publish_date && mergedJson.publish_date !== "Not available in transcript" && mergedJson.publish_date !== "Not provided in transcript") ? mergedJson.publish_date : vidIdentData.publish_date;

    const videoIdentification = `ðŸŽ¥ *Video Identification:*
â€¢ *Video Title:* ${videoTitle || 'N/A'}
â€¢ *Channel Name:* ${channelName || 'N/A'}
â€¢ *Publish Date:* ${publishDate || 'N/A'}`;

    // Core Topics & Thesis
    const coreData = data.core_topics_and_thesis || {};
    const mainSubjects = getSafeArray(coreData.main_subjects_discussed);
    const centralThesis = coreData.central_thesis_or_argument || 'N/A';

    const coreTopicsThesis = `ðŸ’¡ *Core Topic(s) & Thesis:*
â€¢ *Core Topic(s):*
${mainSubjects.length > 0 ? mainSubjects.map(s => `  â€¢ ${s}`).join('\n') : '  â€¢ N/A'}
â€¢ *Thesis:* ${centralThesis}`;

    // Key Discussion Points & Evidence
    const kdpData = data.key_discussion_points_and_evidence || {};
    const primaryArgs = getSafeArray(kdpData.primary_arguments_or_insights);
    const supportingNotes = getSafeArray(kdpData.supporting_data_or_evidence_notes);
    const mainTakeaways = getSafeArray(kdpData.main_takeaways_for_investors);

    const keyDiscussionPoints = `ðŸ’¬ *Key Discussion Points & Evidence:*
â€¢ *Primary Arguments/Insights:*
${primaryArgs.length > 0 ? primaryArgs.map(p => `  â€¢ ${p}`).join('\n') : '  â€¢ N/A'}
â€¢ *Supporting Data/Evidence Notes:*
${supportingNotes.length > 0 ? supportingNotes.map(s => `  â€¢ ${s}`).join('\n') : '  â€¢ N/A'}
â€¢ *Main Takeaways for Investors:*
${mainTakeaways.length > 0 ? mainTakeaways.map(t => `  â€¢ ${t}`).join('\n') : '  â€¢ N/A'}`;

    // Assets Mentioned Details
    // CORRECTED: Ensure assetsArray is always an array before calling .map
    const assetsDetailsData = data.assets_mentioned_details;
    const assetsArray = Array.isArray(assetsDetailsData) ? assetsDetailsData : [];

    const assetsMentioned = `ðŸ’° *Assets Mentioned & Analysis Context (If applicable):*
${assetsArray.length > 0 ? assetsArray.map(asset => {
        return `â€¢ *Asset:* ${asset.asset_name_or_ticker || 'N/A'}\n  â€¢ *Discussion Summary:* ${asset.discussion_summary_and_analysis || 'N/A'}`;
    }).join('\n\n') : 'â€¢ *No specific assets mentioned or analyzed.*'}`;

    // Predictions and Recommendations
    const predData = data.predictions_and_recommendations || {};
    const predictions = getSafeArray(predData.explicit_predictions_or_forecasts);
    const actionsSuggested = getSafeArray(predData.investment_recommendations);
    const rationaleForRecs = getSafeArray(predData.rationale_for_recommendations);
    const biasesRisks = getSafeArray(predData.potential_biases_risks_limitations);

    const predictionsRecommendations = `ðŸ”® *Predictions/Recommendations & Rationale (If applicable):*
â€¢ *Specific Predictions/Forecasts:*
${predictions.length > 0 ? predictions.map(p => `  â€¢ ${p}`).join('\n') : '  â€¢ N/A'}
â€¢ *Investment Actions Suggested:*
${actionsSuggested.length > 0 ? actionsSuggested.map(a => `  â€¢ ${a}`).join('\n') : '  â€¢ N/A'}
â€¢ *Rationale for Recommendations:*
${rationaleForRecs.length > 0 ? rationaleForRecs.map(r => `  â€¢ ${r}`).join('\n') : '  â€¢ N/A'}
â€¢ *Identified Biases, Risks, or Limitations:*
${biasesRisks.length > 0 ? biasesRisks.map(b => `  â€¢ ${b}`).join('\n') : '  â€¢ N/A'}`;

    // Overall Sentiment and Tone
    const sentimentData = data.overall_sentiment_and_tone || {};
    const sentimentText = sentimentData.general_tone_and_sentiment || 'N/A';
    const analyticalTone = sentimentData.analytical_tone_observed || 'N/A'; // This key is not in your example LLM output, so it will be N/A
    const rigorNotesText = sentimentData.analytical_rigor_assessment || 'N/A';

    let sentimentEmoji = 'âšªï¸ ';
    const lowerSentimentText = sentimentText.toLowerCase();
    const positiveKeywords = ['bullish', 'positive', 'optimistic', 'contrarian bullish', 'cautiously optimistic'];
    const negativeKeywords = ['bearish', 'negative', 'cautious'];
    const neutralKeywords = ['neutral', 'mixed'];

    if (positiveKeywords.some(k => lowerSentimentText.includes(k))) {
        sentimentEmoji = 'ðŸŸ¢ ';
    } else if (negativeKeywords.some(k => lowerSentimentText.includes(k))) {
        sentimentEmoji = 'ðŸ”´ ';
    } else if (neutralKeywords.some(k => lowerSentimentText.includes(k))) {
        sentimentEmoji = 'ðŸŸ¡ ';
    }

    const overallSentimentTone = `ðŸ“Š *Overall Sentiment & Analytical Tone:*
â€¢ *Sentiment:* ${sentimentEmoji} ${sentimentText}
â€¢ *Analytical Tone:* ${analyticalTone}
â€¢ *Analytical Rigor Notes:* ${rigorNotesText}`;

    // Executive Summary
    const executiveSummary = `ðŸ“ *Executive Summary:*
${data.executive_summary || 'N/A'}`;


    // --- Splitting Logic (remains the same) ---
    const sections = [
        videoIdentification,
        coreTopicsThesis,
        keyDiscussionPoints,
        assetsMentioned,
        predictionsRecommendations,
        overallSentimentTone,
        executiveSummary
    ];
    let currentMessagePart = '';
    let messageCounter = 1;
    for (const section of sections) {
        const separator = currentMessagePart === '' ? '' : '\n\n---\n\n';
        if (currentMessagePart.length + separator.length + section.length > TELEGRAM_MAX_LENGTH) {
            if (section.length > TELEGRAM_MAX_LENGTH) {
                let tempSection = section;
                while (tempSection.length > TELEGRAM_MAX_LENGTH) {
                    allTelegramMessages.push({ json: { text: `*(Part ${messageCounter++})*\n\n` + tempSection.substring(0, TELEGRAM_MAX_LENGTH), video_id: currentVideoId } });
                    tempSection = tempSection.substring(TELEGRAM_MAX_LENGTH);
                }
                if (tempSection.length > 0) { allTelegramMessages.push({ json: { text: `*(Part ${messageCounter++})*\n\n` + tempSection, video_id: currentVideoId } }); }
                currentMessagePart = ''; continue;
            }
            allTelegramMessages.push({ json: { text: `*(Part ${messageCounter++})*\n\n` + currentMessagePart, video_id: currentVideoId } });
            currentMessagePart = section;
        } else {
            currentMessagePart += separator + section;
        }
    }
    if (currentMessagePart.length > 0) {
        allTelegramMessages.push({ json: { text: `*(Part ${messageCounter++})*\n\n` + currentMessagePart, video_id: currentVideoId } });
    }
}
return allTelegramMessages;
